let mobileNavButton=document.querySelector(".mobile-nav__button"),mobileNavButtonClose=document.querySelector(".mobile-nav__button--close"),mobileNavMenu=document.querySelector(".mobile-nav__menu-holder");mobileNavButton.addEventListener("click",function(){mobileNavButton.classList.add("display"),mobileNavMenu.classList.remove("display")}),mobileNavButtonClose.addEventListener("click",function(){mobileNavButton.classList.remove("display"),mobileNavMenu.classList.add("display")}),document.addEventListener("keydown",e=>{switch(e.key){case"Escape":case"Esc":window.location.hash="#close";break;case"Left":case"ArrowLeft":const o=document.querySelector(window.location.hash);if(!o)return;const t=o.previousElementSibling;if(!t)return;window.location.hash=`#${t.id}`;break;case"Right":case"ArrowRight":const n=document.querySelector(window.location.hash);if(!n)return;const i=n.nextElementSibling;if(!i)return;window.location.hash=`#${i.id}`;break;default:return}});